import React from "react";

const InvestmentForm=()=>{
    const [currentSavings, setCurrentSavings] = useState('');
    const [yearlySavings, setYearlySavings] = useState('');
    const [expectedInterest, setExpectedInterest] = useState('');
    const [investmentDuration, setInvestmentDuration] = useState('');
    
  
    const currentSavingsHandler = (event) => {
      setCurrentSavings(event.target.value);
      
    };
  
    const yearlySavingsHandler = (event) => {
      setYearlySavings(event.target.value);
      
    };
  
    const expectedInterestHandler = (event) => {
      setExpectedInterest(event.target.value);
      
    };
    const investmentDurationHandler = (event) => {
        setInvestmentDuration(event.target.value);
        
      };
  
    const submitHandler = (event) => {
      event.preventDefault();
  
      const expenseData = {
        title: currentSavings,
        amount: yearlySavings,
        date: new Date(expectedInterest),
      };
  
      props.onSaveExpenseData(expenseData);
      setCurrentSavings('');
      setYearlySavings('');
      setExpectedInterest('');
    };
return(
    <form className="form">
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" value={currentSavings}
            onChange={currentSavingsHandler}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" value={yearlySavings}
            onChange={yearlySavingsHandler}/>
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" value={expectedInterest}
            onChange={expectedInterestHandler}/>
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" value={investmentDuration}
            onChange={investmentDurationHandler}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
);

};

export default InvestmentForm;
